INSERT INTO `diary` VALUES (193, '粉色浪漫，梅花情缘，相约南京溧水，共谱一个春日的故事', '序篇 看，是春天的气息 温暖的阳光，唤醒的世界， 花开的芬芳，草莓的甜蜜~ 开启 春日 的最佳方式， 是去自然中寻找春的身影， 这次，在 溧水 ， 我找到了~ 受疫情影响，从去年的10月开始就基本没有安排个人的出行游玩了。趁着年后人少和当时江浙沪情况稳定，天气又回暖了，带...', 'https://note.mafengwo.net/img/a4/60/3f1080d918ef39efb458c0e8b001cec4.jpeg', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:02', '2024-04-10 15:16:02', NULL, NULL, -1, NULL, 0);
