INSERT INTO `diary` VALUES (286, '五刷南京，微火慢炖，秋色，六朝，民国，名邸', '篇前 杭州 有 上海 后花园之称， 南京 作 上海 的前花园当之无愧。这里有山，有湖，有江；有风景有历史有人文有底蕴，帝王气十足；这是一座值得细品慢用的城市，放缓脚步，静心体会；一年四季，季季有景。2019年深秋，我们第五次来到 南京 。沦陷九天，超长巡航，践行慢稳。归...', 'https://p1-q.mafengwo.net/s15/M00/8B/D2/CoUBGV4cI9aAXrIkAAsmL6qCbIw417.JPG', '风景', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:05', '2024-04-10 15:16:05', NULL, NULL, -1, NULL, 0);
