INSERT INTO `diary` VALUES (516, '南京无锡闺蜜游     去金陵——看这座城市沉浮', '前言 对于金陵 南京 的印象还停留在杜牧的那句 南朝四百八十寺。 多少楼台烟雨中。。 本想找点更有深度的诗句。。 算了。。 还是不装文人。。 这次秋季 南京 之行让我充分感受到这座古都的历史文化气息。。。 可以慢慢去了解它沉淀的过程。。。 我们的行程以佛系为主。。 其实...', 'https://p1-q.mafengwo.net/s17/M00/AA/76/CoUBXl-2AzyAOs_OAA32B2_1zYc434.jpg?imageMogr2%2Fthumbnail%2F%21250x185r%2Fgravity%2FCenter%2Fcrop%2F%21250x185%2Fquality%2F90', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:13', '2024-04-10 15:16:13', NULL, NULL, -1, NULL, 0);
