INSERT INTO `diary` VALUES (538, '暑假畅游新加坡', '行程 打卡景点： Day1： 天津 - 新加坡 Day2：环球影城-糖果店 Day3：圣淘沙岛-sea海洋馆- 皮克林 宾乐雅酒店-牛车水 Day4：小 印度 -旧禧街警察局- 圣安德烈 教堂-滨海湾公园 Day5：动物园-乌节路 Day6：鱼尾狮公园-牛车水 Day7：樟宜国际机场 好吃的很多，几天吃过来我最喜欢...', 'https://p1-q.mafengwo.net/s12/M00/EF/A7/wKgED1w5afCAD-daAAaG_USp4xo35.jpeg', '美食', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:14', '2024-04-10 15:16:14', NULL, NULL, -1, NULL, 0);
