INSERT INTO `diary` VALUES (583, '2024寒假南京3日游', '女儿作为高中生，假期虽然紧张，但也想让她放松一下，所以计划出五六天的行程，最终定到了 南京 。本想在这里进行深度游玩，怎奈看天去预报出游的这几天降温有雨，所以攻略做了一遍又一遍，最后决定去一趟 苏州 吧，可 苏州 又买不到回来的车票， 整个的行程变成了 南京 — 苏...', 'https://note.mafengwo.net/img/9b/ec/449ca3b97adc32e375aea4d9ba79ec9d.jpeg', '攻略', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:15', '2024-04-10 15:16:15', NULL, NULL, -1, NULL, 0);
