INSERT INTO `diary` VALUES (642, '#消夏计划#回到金陵', '从火车站出来，清风扑面，显然大雨已在炎夏给这金陵城降温。这个夏天再次回到 南京 。从车站走到对面的玄武湖，就直接拍下这张照片，绿的饱满的树，雾气朦胧的湖水，还有背着书包的青年。就想起十几年前那个夏天第一次踏足这里的我，青春年少，意气风发的来到这 个旧 时都城开...', 'https://p1-q.mafengwo.net/s8/M00/5B/52/wKgBpVWSTaqAKTCoAAbH1SRe23836.jpeg?imageMogr2%2Fthumbnail%2F%21250x185r%2Fgravity%2FCenter%2Fcrop%2F%21250x185%2Fquality%2F90', '交通', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:17', '2024-04-10 15:16:17', NULL, NULL, -1, NULL, 0);
INSERT INTO `diary` VALUES (643, '南京印象—金陵十二钗的乱点鸳鸯谱', '时隔三年再次邂逅 南京 依旧是初冬，没有北国的破败萧索，只有 江南 的郁葱繁盛。无怪乎先后六朝（东吴、东晋、宋、齐、梁、陈）在此建都。 南京 让人印象最深的除了厚重的历史，还有因为爱情而遍种全城的 法国 梧桐。即使二刷 南京 ，也还有一些著名的地方未曾涉足，而所有这...', 'https://p1-q.mafengwo.net/s16/M00/C6/37/CoUBUmARcPaAVbQCAAeK4gHjmHg029.jpg', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:17', '2024-04-10 15:16:17', NULL, NULL, -1, NULL, 0);
