INSERT INTO `diary` VALUES (673, '西班牙、葡萄牙游记之四--马德里', '马德里 是 西班牙 首都及最大的城市，位于 西班牙 国土中部，曼萨纳雷斯河贯穿其中，在历史上因战略位置重要而素有“ 欧洲 之门”的称号。其建城于9世纪，是在摩尔人边贸站“马格立特”旧址上发展起来的城市；1561年， 西班牙 国王腓力二世将首都从 托莱多 迁入于此，由于其特...', 'https://p1-q.mafengwo.net/s11/M00/74/A9/wKgBEF2G67iARPYgAAmM9WAiF-Y062.jpg', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:19', '2024-04-10 15:16:19', NULL, NULL, -1, NULL, 0);
