INSERT INTO `diary` VALUES (693, '南京3日游｜深秋入金陵，处处美如画', '这趟 南京 行是因为6月份囤的酒店券🏨 疫情恢复初期的旅行券真的无敌了，还有不少券因为没有假期了，只能算曾经拥有过。 南京 的深秋，是上了色的画卷， 老街的梧桐，秦淮河边的银杏，白墙黑瓦的老宅，历经岁月的洋房，庙宇集市的红墙…… 秋日的阳光洒落下来，就是绝美的秋色...', 'https://p1-q.mafengwo.net/s17/M00/2D/EB/CoUBXl_GVsOAIyn2ABUMJ7NG8RI709.jpg', '住宿', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:19', '2024-04-10 15:16:19', NULL, NULL, -1, NULL, 0);
