INSERT INTO `diary` VALUES (787, '【首贴】单身旅记-跟我走好好吃(吃在广州 )', '＂食在 广州 ，厨出 凤城 。＂这句话早就听说过。而自从在南航的航班上一口气看完《寻味 顺德 》后，就更是抑不住吃游 广州 的念头 。奈何一直没有合适的机会！想啊念啊，却无法成行！终于，这个冬天，爱吃的老阿姨得以一偿夙愿，用五天的时间，慰藉一颗吃货的心。 本文全部行...', 'https://p1-q.mafengwo.net/s12/M00/B3/09/wKgED1wiQiCAOkwiABeptI4nBHM49.jpeg?imageMogr2%2Fthumbnail%2F%21250x185r%2Fgravity%2FCenter%2Fcrop%2F%21250x185%2Fquality%2F90', '美食', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:23', '2024-04-10 15:16:23', NULL, NULL, -1, NULL, 0);
