INSERT INTO `diary` VALUES (805, '今日朝鲜～它的神秘在于你的一举一动都被监视着', '先发几张非旅游景点的照片 这样的 朝鲜 ，你想不想去看看呢？ 马卡龙颜色的建筑风格 尖尖屋顶的建筑是臭名昭著的烂尾工程～柳京饭店 岛中心的建筑是羊角岛酒店，它是第一个涉外酒店 新人结婚，一定要去万寿台广场向两位已故领袖献花，鞠躬，合影拍照 这是我2012年去 平壤 时拍...', 'https://p1-q.mafengwo.net/s14/M00/5E/92/wKgE2l2CSnWAS8GYAAtwzzjX0qk092.jpg?imageMogr2%2Fthumbnail%2F%21250x185r%2Fgravity%2FCenter%2Fcrop%2F%21250x185%2Fquality%2F90', '风景', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:23', '2024-04-10 15:16:23', NULL, NULL, -1, NULL, 0);
