INSERT INTO `diary` VALUES (860, '烟雨濛濛：2019春季沪宁行', '前言 过了春节，公司安排去 上海 出差，办完了正经事儿正好可以放松放松，在 上海 和 南京 转一下。主要去了以下几个地方： 1. 上海 ：真如寺、静安寺和 上海 博物馆 2. 南京 ： 南京 博物院、灵谷寺、大报恩寺遗址公园、朝天宫、 江宁 织造博物馆、六朝博物馆和明故宫遗址公园...', 'https://p1-q.mafengwo.net/s13/M00/8A/D0/wKgEaVyKbs2AAsOgAAt2X6pnI7855.jpeg', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:26', '2024-04-10 15:16:26', NULL, NULL, -1, NULL, 0);
