INSERT INTO `diary` VALUES (938, '中国20世纪建筑遗产 中国三大博物院--南京博物院', '南京 博物院是 中国 三大博物馆之一 ，简称南院或南博，其前身是民国二十二年（1933年）蔡元培等倡建的国立中央博物院，是 中国 创建最早的博物馆、 中国 第一座由国家投 资兴 建的大型综合类博物馆 ，现为大型综合性的国家级博物馆、首批国家一级博物馆 。首批中央地方共建国...', 'https://note.mafengwo.net/img/c4/77/e12b56f11948a841bc63715cf1d7fc22.jpeg', '其他', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:29', '2024-04-10 15:16:29', NULL, NULL, -1, NULL, 0);
