INSERT INTO `diary` VALUES (317, '初秋的南京，是梧桐落叶的声音', '“那年，她说她喜欢 法国 梧桐，他就把梧桐种满了整个 南京 ” “最良心的景点一个是陕博，另一个就是 中山 陵” “国家宝藏里介绍的 南京 博物院” “鸭血粉丝，美龄粥，盐水鸭...” “ 山阴 路的夏天” “致青春” ...... 众多因素吸引着，就不得不去看看这座城市了 一座六朝...', 'https://p1-q.mafengwo.net/s12/M00/72/8F/wKgED1vlV_mAaXPjABAMN5D_BQ466.jpeg', '风景', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:06', '2024-04-10 15:16:06', NULL, NULL, -1, NULL, 0);
