INSERT INTO `diary` VALUES (352, '南京｜追忆历史足迹，古都两天两晚亲子漫步', '🌿前几次带着孩子们来六朝古都——金陵旅行都正逢小长假，各处景点游人络绎不绝，上次去总统府和博物馆没有预约到门票。今年在孩子们放寒假的第一周，我们再次来到 南京 ，尽管市区内的核心景区依然有很多游客，但相对于暑期和小长假期间来说，旅行体验已经好了很多。 🌿时间安...', 'https://note.mafengwo.net/img/8f/fd/524e4395918dabf923bef9a25c8ac69c.jpeg', '风景', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:07', '2024-04-10 15:16:07', NULL, NULL, -1, NULL, 0);
