INSERT INTO `diary` VALUES (500, '走进高淳，打卡网红打卡地固城湾景区，品尝固城湖螃蟹', '“秋风起，蟹脚痒”，又到一年吃蟹时！ 说起吃蟹，对于 南京 人及周边人而言，固城湖大闸蟹那是一定不可错过的美味，历来素有“蟹中之冠”的美誉，更是国家地理标志性产品。 固城湖水域方圆百里，碧波荡漾，水草丰茂，水质优良，是生长螃蟹的理想之所；不止如此，固城湖风光也...', 'https://p1-q.mafengwo.net/s18/M00/CF/D9/CoUBYGFYOpuAUar6AAvr-P-X5fs244.jpg', '美食', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:12', '2024-04-10 15:16:12', NULL, NULL, -1, NULL, 0);
