INSERT INTO `diary` VALUES (919, '冬游南京', '南京 是一座散发着优雅古韵的城市，承载着丰富的历史遗产和人文景观。我们也被 南京 的独特文化所吸引，于是沐浴着冬日的暖阳，驱车来到这座瑰宝般的城市。 总统府 南京 作为六朝古都，在近代史上发挥着重要作用，想要了解 南京 的历史，一定从 南京 总统府开始。 远远就能看到...', 'https://note.mafengwo.net/img/20/61/1767e266ef5d0111265d86870a8e6ef5.jpeg', '风景', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:28', '2024-04-10 15:16:28', NULL, NULL, -1, NULL, 0);
