INSERT INTO `diary` VALUES (928, '夫子庙的幸福', '南京 ，一座历史悠久的文化古城，以其美丽的风景和丰富的美食吸引着无数游客。翠隆斋汤包，作为 南京 的特色小吃之一，更是让人流连忘返。今天，我们就跟随程大美女来到 南京 夫子庙，品尝这美味的翠隆斋汤包。 翠隆斋汤包，源于光绪三十二年，其制作工艺独特，选料讲究，味道...', 'https://note.mafengwo.net/img/bb/6e/3cca6647890382757b16d0feae2c4c59.jpeg', '美食', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:28', '2024-04-10 15:16:28', NULL, NULL, -1, NULL, 0);
