INSERT INTO `diary` VALUES (1001, '南京1日游2017', '（游记搬家中。。。） 2017年3月3日上海-南京 南京站 南京 去过多次，但印象最深的是1999年，带读小学的女儿，在 南京 玩了几天， 南京 的亲属陪着去了明城墙、珍珠泉、 中山 陵等景点，女儿的话还记忆犹新： 南京 的梧桐树真好，都是手拉手；还有就是： 南京 十字路口有噪声显...', 'https://note.mafengwo.net/img/9f/6c/908e1d811f614ddcec24aeb18063db81.png', '攻略', 'o4VqY5sJfSWnl92Btl0YXphUdafQ', '2024-04-10 15:16:31', '2024-04-10 15:16:31', NULL, NULL, -1, NULL, 0);
